import React, { useState, useRef, useEffect } from 'react';

// S3 bucket name and API endpoint
const S3_BUCKET = process.env.REACT_APP_S3_BUCKET;
const API_ENDPOINT = process.env.REACT_APP_API_ENDPOINT;

function App() {
  const [file, setFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadStatus, setUploadStatus] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [highlightVideo, setHighlightVideo] = useState(null);
  const [s3Key, setS3Key] = useState('');
  const [dragActive, setDragActive] = useState(false);
  const fileInputRef = useRef(null);
  const pollingIntervalRef = useRef(null);

  // Clean up polling interval on component unmount
  useEffect(() => {
    return () => {
      if (pollingIntervalRef.current) {
        clearInterval(pollingIntervalRef.current);
      }
    };
  }, []);

  // Handle file selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'video/mp4') {
      setFile(selectedFile);
      setUploadStatus('');
      setHighlightVideo(null);
    } else {
      setUploadStatus('error');
      setFile(null);
      alert('Please select an MP4 video file.');
    }
  };

  // Handle drag events
  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  // Handle drop event
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const droppedFile = e.dataTransfer.files[0];
      if (droppedFile.type === 'video/mp4') {
        setFile(droppedFile);
        setUploadStatus('');
        setHighlightVideo(null);
      } else {
        setUploadStatus('error');
        setFile(null);
        alert('Please select an MP4 video file.');
      }
    }
  };

  // Handle file upload
  const handleUpload = async () => {
    if (!file) {
      alert('Please select a file first.');
      return;
    }

    try {
      setIsUploading(true);
      setUploadProgress(0);
      setUploadStatus('uploading');

      // Generate a unique filename with timestamp and random ID
      const timestamp = Math.floor(Date.now() / 1000).toString();
      const randomId = Math.random().toString(36).substring(2, 10);
      const cleanedFilename = file.name.replace(/[^a-zA-Z0-9._-]/g, '');
      const s3Key = `input-videos/${timestamp}-${randomId}-${cleanedFilename}`;
      setS3Key(s3Key);

      // Get presigned URL from API Gateway
      console.log('Requesting presigned URL from:', API_ENDPOINT);
      const response = await fetch(API_ENDPOINT, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          fileName: s3Key,
          contentType: file.type
        }),
      });

      console.log('Presigned URL response status:', response.status);
      const responseText = await response.text();
      console.log('Response text:', responseText);

      if (!response.ok) {
        throw new Error(`Failed to get presigned URL: ${response.status} - ${responseText}`);
      }

      const data = JSON.parse(responseText);
      console.log('Parsed response data:', data);
      const presignedUrl = data.presignedUrl;

      if (!presignedUrl) {
        throw new Error('No presigned URL returned from API');
      }

      console.log('Using presigned URL:', presignedUrl);

      // Use XMLHttpRequest for upload progress tracking
      const xhr = new XMLHttpRequest();
      xhr.open('PUT', presignedUrl);
      xhr.setRequestHeader('Content-Type', file.type);

      xhr.upload.onprogress = (event) => {
        if (event.lengthComputable) {
          const percentCompleted = Math.round((event.loaded * 100) / event.total);
          setUploadProgress(percentCompleted);
        }
      };

      xhr.onload = () => {
        if (xhr.status === 200) {
          setUploadStatus('success');
          setIsUploading(false);
          setIsProcessing(true);

          // Start polling for highlight video
          startPollingForHighlight(s3Key);
        } else {
          throw new Error(`Upload failed with status: ${xhr.status}`);
        }
      };

      xhr.onerror = () => {
        throw new Error('Upload failed due to network error');
      };

      xhr.send(file);
    } catch (error) {
      console.error('Error uploading file:', error);
      setUploadStatus('error');
      setIsUploading(false);
    }
  };

  // Poll for highlight video
  const startPollingForHighlight = (videoKey) => {
    if (pollingIntervalRef.current) {
      clearInterval(pollingIntervalRef.current);
    }

    let attempts = 0;
    const maxAttempts = 30; // Poll for up to 5 minutes (30 * 10 seconds)

    pollingIntervalRef.current = setInterval(async () => {
      attempts++;

      try {
        // Extract timestamp and ID from the S3 key
        // Format: input-videos/TIMESTAMP-ID-filename.mp4
        const keyParts = videoKey.split('/');
        const filename = keyParts[keyParts.length - 1];
        const filenameParts = filename.split('-');

        if (filenameParts.length >= 2) {
          const timestamp = filenameParts[0];
          const id = filenameParts[1];

          // Check if highlight video exists
          const highlightKey = `highlight-videos/${timestamp}-${id}-highlights.mp4`;

          // Try to get the highlight video
          const highlightUrl = `https://${S3_BUCKET}.s3.amazonaws.com/${highlightKey}`;

          // Make a HEAD request to check if the file exists
          try {
            const response = await fetch(highlightUrl, { method: 'HEAD' });

            if (response.ok) {
              // If we get here, the file exists
              setHighlightVideo(highlightUrl);
              setIsProcessing(false);
              clearInterval(pollingIntervalRef.current);
            } else {
              throw new Error('File not found');
            }
          } catch (error) {
            // File doesn't exist yet, continue polling
            console.log(`Highlight video not ready yet. Attempt ${attempts}/${maxAttempts}`);

            if (attempts >= maxAttempts) {
              setIsProcessing(false);
              setUploadStatus('error');
              clearInterval(pollingIntervalRef.current);
              alert('Highlight generation timed out. Please try again later.');
            }
          }
        }
      } catch (error) {
        console.error('Error polling for highlight:', error);

        if (attempts >= maxAttempts) {
          setIsProcessing(false);
          setUploadStatus('error');
          clearInterval(pollingIntervalRef.current);
          alert('Highlight generation timed out. Please try again later.');
        }
      }
    }, 10000); // Poll every 10 seconds
  };

  return (
    <div className="container">
      <div className="header">
        <h1>Video Highlights Generator</h1>
      </div>

      <div className="upload-container">
        <div 
          className={`drop-zone ${dragActive ? 'active' : ''}`}
          onClick={() => fileInputRef.current.click()}
          onDragEnter={handleDrag}
          onDragOver={handleDrag}
          onDragLeave={handleDrag}
          onDrop={handleDrop}
        >
          <input 
            type="file" 
            ref={fileInputRef}
            onChange={handleFileChange}
            accept="video/mp4"
            style={{ display: 'none' }}
          />
          {file ? (
            <p>Selected file: {file.name}</p>
          ) : (
            <p>Drag and drop an MP4 video file here, or click to select a file</p>
          )}
        </div>

        {file && !isUploading && !isProcessing && !highlightVideo && (
          <button 
            onClick={handleUpload}
            style={{
              padding: '10px 20px',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              fontSize: '16px',
              width: '100%'
            }}
          >
            Upload Video
          </button>
        )}

        {isUploading && (
          <div className="progress-container">
            <div className="progress-bar">
              <div 
                className="progress-bar-fill" 
                style={{ width: `${uploadProgress}%` }}
              ></div>
            </div>
            <p>Uploading: {uploadProgress}%</p>
          </div>
        )}

        {uploadStatus === 'success' && !isProcessing && !highlightVideo && (
          <div className="status-message success">
            <p>Upload successful!</p>
          </div>
        )}

        {uploadStatus === 'error' && (
          <div className="status-message error">
            <p>Error uploading file. Please try again.</p>
          </div>
        )}

        {isProcessing && (
          <div className="status-message info">
            <p>Processing transcription and highlights... This may take a few minutes.</p>
          </div>
        )}

        {highlightVideo && (
          <div className="video-container">
            <h2>Your Highlight Video</h2>
            <video controls>
              <source src={highlightVideo} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
